var mysql = require('mysql');

function connectServer() {

    var client = mysql.createConnection({
        host: 'localhost',
        user: 'root',
        password: '',
        database: 'mysql'
    })

    return client;
}
function selectFun(client, username, callback) {
    //client为一个mysql连接对象
    client.query('select password from person where username="' + username + '"', function (err, results, fields) {
        if (err) throw err;
        callback(results);
    });
}
function insertFun(client, username, password, phoneNum,callback) {
    client.query('insert into person (id,name,sex,password,username,iponeNum) values ("2","xiao","男","'+password+'","'+username+'","'+phoneNum+'")', function (err, result) {
        if (err) {
            console.log("error:" + err.message);
            return err;
        }
        callback(err);
    });
}

exports.connect = connectServer;
exports.selectFun = selectFun;
exports.insertFun = insertFun;